import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';
import { MapUtils } from '../utils/MapUtils';
import { CovidDataService } from '../service/CovidDataService';

const AnyReactComponent = ({ text }) => <div>{text}</div>;

class CovidMap extends Component {
  static defaultProps = {
    center: {
      lat: 40,
      lng: -95
    },
    zoom: 6
  };

  state = {
    zoomLevel: 6,
    boundary: {},
    points: {}
  }

  render() {
    return (
      // Important! Always set the container height explicitly
      <div style={{ height: '100vh', width: '100%' }}>
        <GoogleMapReact
          bootstrapURLKeys={{ key: "AIzaSyDzt6zlUyk1nOEziTAvI5gqymDhup5S9qg" }}
          defaultCenter={this.props.center}
          defaultZoom={this.props.zoom}
          onGoogleApiloaded={
              () => {
                  CovidDataService.getAllCountyCases()
                    .then(response => {
                        this.setState({
                            points: MapUtils.convertCovidPoints(response.data)
                        })

                    }).catch(error => {
                        console.error(error);
                    })
              }
          }
          onChange={(changeEventObject) => {
            this.setState({
                zoomLevel: changeEventObject.zoom,
                boundary: changeEventObject.bounds
            });
          }}
        >
          <AnyReactComponent
            lat={59.955413}
            lng={30.337844}
            text="My Marker"
          />
          <AnyReactComponent
            lat={59.955413}
            lng={30.337844}
            text="My Marker"
          />
          <AnyReactComponent
            lat={59.955413}
            lng={30.337844}
            text="My Marker"
          />
          {this.renderCovidPoints()}
        </GoogleMapReact>
      </div>
    );
  }
  renderCovidPoints() {
    const result = [];
    const zoomLevel = this.state.zoomLevel;
    // 1-4 nation level
    // 5-9 state level
    // 10-20 county level
    if (zoomLevel < 1 || zoomLevel > 20) {
        return result;
    }
    let pointsLevel = 'county';
    if (zoomLevel >= 1 && zoomLevel <= 9) {
        pointsLevel = 'nation';
    } else if (zoomLevel > 4 && zoomLevel <= 9) {
        pointsLevel = 'state';
    }
    const pointsToRender = this.state.points[pointsLevel];

    if (!pointsToRender) {
        return result;
    }
  }
}

export default CovidMap;
